import React, { useState } from "react";


function BabyNamesList(props){

  const [babyName, setBabyName] = useState("");

  const [favorites, setFavorites] = useState([null]);

  const addFavorites = () => {

    setFavorites(favorites.push(babyName));
  }



    return (
      <>
        <ul>
          <p>
            Favorites:
            {favorites.map((favorite, i) => (
              <li key={i}>{favorite}</li>
            ))}
          </p>
        </ul>
        <hr />
        <ul>
          {props.data.map((babyName, index) => (
            <>
              {babyName.sex === 'm' ? (
                <li
                  onClick={(e) => addFavorites(setBabyName(babyName.name))}
                  className="genderFormatBoy"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              ) : (
                <li
                  onClick={(e) => addFavorites(setBabyName(babyName.name))}
                  className="genderFormatGirl"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              )}
            </>
          ))}
        </ul>
      </>
    );
}


export default BabyNamesList;
