import React, { useState } from "react";


function BabyNamesList(props){

  const [babyName, setBabyName] = useState("");

  const [addFavorite, setAddFavorite] = useState([]);

  const add = () => {
    setAddFavorite(addFavorite.concat([babyName]));
  }

    return (
      <>
        <p>
          {' '}
          Favorites:
          <ul>
            {addFavorite.map((v, i) => (
              <li key={i}>{v.babyName}</li>
            ))}
          </ul>
        </p>
        <hr />
        <ul>
          {props.data.map((babyName) => (
            <>
              {babyName.sex === 'm' ? (
                <li
                  onClick={(e) => setBabyName(babyName.name)}
                  className="genderFormatBoy"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              ) : (
                <li
                  onClick={(e) => add(setBabyName(babyName.name))}
                  className="genderFormatGirl"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              )}
            </>
          ))}
        </ul>
      </>
    );
}


export default BabyNamesList;
