import React, { useState } from "react";


function BabyNamesList(props){

   const [addFavorite, setAddFavorite] = useState([]);

   function chooseBabyFavoriteNames(babyName){
    setAddFavorite(addFavorite.concat(babyName.id));
   }

    return (
      <>
        <p>Favorites: {addFavorite}</p>
        <hr />
        <ul>
          {props.data.map((babyName) => (
            <>
              {babyName.sex === 'm' ? (
                <li
                  onClick={(e) => chooseBabyFavoriteNames(babyName.name)}
                  className="genderFormatBoy"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              ) : (
                <li
                  onClick={(e) => chooseBabyFavoriteNames(babyName.na)}
                  className="genderFormatGirl"
                  key={babyName.id}
                >
                  {babyName.name}
                </li>
              )}
            </>
          ))}
        </ul>
      </>
    );
}


export default BabyNamesList;
